import React from 'react'
import { hslaStr, randomSize } from './utils/helpers'

const Dummy = (props) => {

	const randmSzStr = randomSize(25,55)
	
	const style = {
		width:randmSzStr,
		height:randmSzStr,
		background:'hsla('+hslaStr()+')',
		top:-5000,
		left:20,
		position:'absolute',
		"&:hover": {
			opacity:"0.5 !important"
		}
	}

	const switchColor = (e) => {
		//e.preventDefault()
		const dummy = e.target
		const color = dummy.style.background
		const wrapper = document.getElementById("mjs-wrapper")
		const origColor = wrapper.style.background
		wrapper.style.background = color
		dummy.style.background = "lightgrey"
		const rePaint = (element, target, color) => {
			element.style.background = origColor
			target.style.background = color
		}
		setTimeout(rePaint,500,wrapper,dummy,color)
	}
	return (
		<div className='blocks dummies super-delayed-fallable xposrandom' onTouchStart={switchColor} onMouseDown={switchColor} style={style}></div>
	)
}

export default Dummy
