import React, { useState } from 'react'
import ZurWand from './../components/zurwand'
import Einzelbild from './../components/einzelbild'

// hier müssen Bilder geladen werden
// const bilder = []
// bilder.map(...) erstelle <BildThumb />


const Bilderwand = props => {
	const [einzelAnsicht, setEinzelAnsicht] = useState(false)

	// ### click auf Bild -> setEinzelAnsicht! && set Bildinfos

	const style = {
		header:{
			display:'flex',
			flexDirection: 'row',
    		alignItems: 'center',
    		justifyContent: 'space-between',
    		top: 0,
    		height: 64,
    		position: 'fixed',
    		zIndex: 1
		},
		heading: {
			height: 36,
			zIndex:2,
			background:'#333333',
			color:'lightgrey',
			pointerEvents: 'none',
			fontSize:'1.5em'
		},
		wand:{
			background:'#2b2b2b',
			height:'100vh',
			position: 'fixed',
		    top: 0,
		    left: 0
		},
		imagewrapper:{
			position:'relative',
			width:'100vw',
			height:'100vw',
			display: 'flex',
		    flexDirection: 'row',
		    alignItems: 'center',
		    justifyContent: 'center'
		},
		image:{
			cursor:'pointer',
			display:'inline',
			opacity:0,
			animationName:'fadein',
			animationDuration:'0.4s',
			animationDelay:'1s',
			animationTimingFunction: 'ease',
			animationFillMode: 'forwards',
			height:'70%',
			webkitBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1), ",
			mozBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1)",
			boxShadow: "0 0 10px rgba(0,0,0,1),0 0 30px rgba(0,0,0,0.85),0 0 40px rgba(0,0,0,0.45),0 0 50px rgba(0,0,0,0.3),0 0 60px rgba(0,0,0,0.25)"
		},
		bildtitel:{
			color: 'lightgrey'
		}
	}

	// clickEvent auf IMG, 
	// dann <p className="bild_titel" style={style.bildtitel}>{props.bilder[0].titel}</p> 
	// unter Einzelbildansicht (EBA)

	// iterator for SAV selection
	let i = 0

	const bilder = props.bilder
	/*
		[i]
			.titel
			.beschreibung
			.preis
			.tags
			.groesse.x/.y/.z
			.gewicht
			.altSrc // [filename2,filename3]
			.source
			.alt#txt
			.verkauft // true,false
	*/

	const einzelbild = () => {
		setEinzelAnsicht(true)
	}

	return (
		<div className="sctn sctn_bilderwand">
			<div className="sctn_header" style={style.header}>
				<h1 className="sctn_heading" style={style.heading}>{props.titel}</h1>
				<ZurWand label="Anmerkungen" wandLaden={() => {console.log(`Wand ${props.titel} wird geladen...`)}}>Anmerkungen der Künstlerin</ZurWand>
			</div>
			<div className="sctn_body">
				<div className="sctn_wand" style={style.wand}>
					<div style={style.imagewrapper}>
						<img 
							className="wandbild" 
							style={style.image} 
							alt={bilder[i].altTxt} 
							src={bilder[i].source} 
							onClick={einzelbild}
						/>
					</div>
					<div style={style.imagewrapper}>
						<img 
							className="wandbild" 
							style={style.image} 
							alt={bilder[i].altTxt} 
							src={bilder[i].source} 
							onClick={einzelbild}
						/>
					</div>
				</div>
			</div>
			{einzelAnsicht && <Einzelbild bildinfos={bilder[i]} schliessen={()=>{setEinzelAnsicht(false)}} />}
		</div>
	)
}

export default Bilderwand
