// Pages
import Home from './pages/home'
import Login from './pages/login'
import Impressum from './pages/impressum'
import Menu from './components/menu'
import Artist from './pages/artist'
import Bilderwand from './pages/bilderwand'








class App extends Component {

  bilder = {
    objekte:'',
    aquarell:'',
    acryl:'',
    suesses:''
  }
  render() {
    return (
      <React.Fragment>
        <div style={{background:"white"}}>
          <Router>
            <Menu /> 
            <div className="container">
              <Routes>
                <Route exact path='/' element={<Home />} />
                <Route exact path='/bilder/aquarelle' element={<Bilderwand titel={'Aquarelle'} tag={'aquarell'} bilder={this.bilder.aquarell} />} />
                <Route exact path='/bilder/objekte' element={<Bilderwand titel={'Objektkunst'} tag={'objekt'} bilder={this.bilder.objekte} />} />
                <Route exact path='/bilder/acryl' element={<Bilderwand titel={'Acrylmalerei'} tag={'acryl'} bilder={this.bilder.acryl} />} />
                <Route exact path='/bilder/suesses' element={<Bilderwand titel={'Süßstoff'} tag={'suesses'} bilder={this.bilder.suesses} />} />

                <Route exact path='/impressum' element={<Impressum />} />
                <Route exact path='/login' element={<Login />} />
                <Route exact path='/kuenstlerin' element={<Artist />} />
              </Routes>
            </div>
          </Router>
        </div>
      </React.Fragment>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);

export default App;
