import React from 'react'
import { Card } from '@material-ui/core'
import { useNavigate } from 'react-router-dom'
import Matter from "matter-js"

class MenuLink extends React.Component {

	constructor(props) {
		// muss dort props rein, in constructor & super?
		super(props)
		this.state = {willNonMenuHtmlbeRemoved:false}
		this.changeHtmlRemove = this.changeHtmlRemove.bind(this)
	}

	navigate = useNavigate()
	
	changeHtmlRemove() {
  		this.setState(prevState => {
  			// immer, wenn der Menüknopf geklickt wird,
  			// und es sich schließen soll,
  			// werden die delayed-fallables mit der class "remove-fallable" versehen.
  			// diese werden in menuDrop.js dann nicht zur Welt hinzugefügt
  			// TODO: if(!menuDrop){foreach delayed-fallable:.classList.add("fallen")}    it.classList.contains("fallen"), remove ) all delayed-fallables will get class  
			return {
				menuDrop:!prevState.menuDrop, manualSet:true
			}			
  		})
  	}

	//const wrld = Matter.Composite.allBodies()
	links = (e) => {e.preventDefault(); if(e.target.classList.contains('clickable')){props.willNonMenuHtmlbeRemoved;e.target.classList.remove('clickable');navigate(props.href)}}
	render(){
		return (
			<Card 
				onTouchStart={(el) => this.links(el)}
				onClick={(el) => this.links(el)} 
				className="blocks menulink fallable noselect xposrandom" 
				style={{
					height:90,
					textAlign: "center",
					display: "flex",
					alignItems:"center",
					textDecoration:"none",
					width:300,
					//backgroundColor:"lightgrey",
					//background: `url(${this.props.imgUrl})`,
					//backgroundPosition: "right",
					backgroundSize:"50%",
					backgroundRepeat: "no-repeat"
			}}
			>	
				<h1>{this.props.titel}</h1>
			</Card>
		)
	}
}
export default MenuLink
