import React from 'react'
import ZurWand from './../components/zurwand'

// hier m端ssen Bilder geladen werden
// const bilder = []
// bilder.map(...) erstelle <BildThumb />


const Bilderwand = (props) => {
	
	const style = {
		header:{
			display:'flex',
			flexDirection: 'row',
    		alignItems: 'center',
    		justifyContent: 'space-between'

		},
		wand:{
			background:'#2b2b2b',
			height:'100%',
			width:'100%',
			position: 'fixed',
		    top: 0,
		    left: 0,
		    display: 'flex',
		    alignItems: 'center',
		    justifyContent: 'center'
		},
		image:{
			height:'400px',
			webkitBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 0.88)",
			mozBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 0.88)",
			boxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 0.88)"
		}
	}
	
	return (
		<div className="sctn sctn_bilderwand">
			<div className="sctn_header" style={style.header}>
				<h1 className="sctn_heading delayed-fallable">{props.titel}</h1>
				<ZurWand label="Anmerkungen der K端nstlerin" wandLaden={() => {console.log(`Wand ${props.titel} wird geladen...`)}}>Anmerkungen der K端nstlerin</ZurWand>
			</div>
			<div className="sctn_body">
				<div className="sctn_wand" style={style.wand}>
					<img style={style.image} src={props.bilder}/>
				</div>
			</div>
		</div>
	)
}

export default Bilderwand
