import React, {Component} from 'react'
import './App.css';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import ReactDOM from 'react-dom'


// Pages
import Home from './pages/home'
import Login from './pages/login'
import Registrierung from './pages/registrierung'
import Menu from './components/menu'


class App extends React.Component {
  constructor() {
    super();

    this.state = {
      height: 0,
      width: 0
    };

    window.addEventListener("resize", this.update);
  }

  componentDidMount() {
    this.update();
  }

  update = () => {
    this.setState({
      height: window.innerHeight,
      width: window.innerWidth
    });
  };

  render() {
    return (
      <React.Fragment>
        <div style={{background:"white",width:"100%",height: "calc(100vh - 50px)",top:"50px",position: "absolute"}}>
          <div style={{display:"flex",justifyContent:"center"}}> 
            <p>height: {this.state.height}px -- </p>
            <p>width: {this.state.width}px</p>
          </div>

          <Router>
            <Menu /> 
            <div className="container">
              <Switch>
                <Route exact path="/" component={Home} />
                <Route exact path="/registrierung" component={Registrierung} />
                <Route exact path="/login" component={Login} />
              </Switch>
            </div>
        </Router>
      </div>

      </React.Fragment>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);

export default App;
