import React from "react"
import Matter from "matter-js"
import MenuLink from './menulink'

class Scene extends React.Component {
	
	constructor(){
		super()
		// TODO: eigentlich muss window state ein paar Stufen höher und ge-propt werden.
		this.state = {
	      	height: window.innerHeight,
	      	width: window.innerWidth
	    };
	    window.addEventListener("resize", this.update);
	}


	style = {
	    wrapper: {
	      display: "block",
	      top:        50,
	      height:     'calc(100% - 50px)',
	      width:      '100vw',
	      position:   'fixed'
	    },
	    menulink: {
	    	// TODO: hier kann man schön conditional logic als breakpoints fahren, abhängig von this.state.width /.height
	    }  
  	}


  	componentDidMount(){

	  	const runCode = () => {

			const listEls = document.querySelectorAll("#mjs-wrapper .menulink")
			
			const engine = Matter.Engine.create()

			const stack = Matter.Composites.stack(
		  		// xx, yy, columns, rows, columnGap, rowGap, cb
		  		0, 0, listEls.length, 1, 0, 0,
		  		(xx, yy, i) => {
		    		const {x, y, width, height} = listEls[i].getBoundingClientRect();
		    		return Matter.Bodies.rectangle(x, y, width, height, {
		      			//isStatic: i === 0 || i + 1 === listEls.length
		    		})
		    	}
		    )
			
		    const floor = Matter.Bodies.rectangle(this.state.width/2,this.state.height-100,this.state.width,50)
		    
		    Matter.Body.setStatic(floor, true)

			const mouseConstraint = Matter.MouseConstraint.create(
	  			engine, {element: document.querySelector("#mjs-wrapper")}
			)


			Matter.World.add(engine.world, [stack, mouseConstraint, floor])

			listEls.forEach(e => {
		  		e.style.position = "absolute";
		  		e.addEventListener("click", e => {
		    		console.log(e.target.textContent)
		    	})
			});

			(function update() {
		  		requestAnimationFrame(update)
			  	stack.bodies.forEach((block, i) => {
				    const li = listEls[i]
				    const {x, y} = block.vertices[0]
				    li.style.top = `${y}px`
				    li.style.left = `${x}px`
				    li.style.transform = `translate(-50%, -50%) 
			                             rotate(${block.angle}rad) 
			                             translate(50%, 50%)`
				});
	  			Matter.Engine.update(engine)
			})();
		}
  	
		runCode()
		this.update()
	}

	update = () => {
    	this.setState({
	      	height: window.innerHeight,
	      	width: window.innerWidth
    	})
    	console.log(this.state)
   	}
	
	render(){
	  	return (
			<div id="mjs-wrapper" style={this.style.wrapper}>
			    <MenuLink href="/impressum"/>
			    <MenuLink />
			    <MenuLink />
			</div>
		)
	}
}
export default Scene
