import React from "react"
import Matter from "matter-js"
import MenuLink from './menulink'
import Dummy from './dummies'

class Scene extends React.Component {
	
	constructor(props){
		super(props)
		// TODO: eigentlich muss window state ein paar Stufen höher und ge-propt werden.
		this.state = {
	      	height: window.innerHeight,
	      	width: window.innerWidth,
	    }
	    window.addEventListener("resize", this.update)
	}

	numberOfDummies = 12
	dummies = () => {
		let dummyStr = ""
		for (var i = this.numberOfDummies.length - 1; i >= 0; i--) {
			dummyStr+='<Dummy/>'
		}
		console.log(dummyStr)
		return dummyStr
	}

	style = {
	    wrapper: {
	      display: "block",
	      top:        50,
	      height:     'calc(100% - 50px)',
	      width:      '100vw',
	      position:   'fixed'
	    },
	    blocks: {},
	    menulink: {
	    	// TODO: hier kann man schön conditional logic als breakpoints fahren, abhängig von this.state.width /.height
	    } 
  	}

  	// TODO: improve click / drag behavior (esp. touch)

  	componentDidMount(){

	  	const runCode = () => {

	  		const Body = Matter.Body
			const listEls = document.querySelectorAll("#mjs-wrapper .menulink")
			const listDummies = document.querySelectorAll("#mjs-wrapper .dummies")
			
			const engine = Matter.Engine.create()
			
			const stack1 = Matter.Composites.stack(
		  		// xx, yy, columns, rows, columnGap, rowGap, cb
		  		0, 0, listEls.length, 1, 0, 0,
		  		(xx, yy, i) => {
		  			const randomX = Math.floor((Math.random() * (this.state.width-200)) + 1)
		    		const {y, width, height} = listEls[i].getBoundingClientRect();
		    		const box = Matter.Bodies.rectangle(randomX, -this.state.height+200, width, height, {
		      			//isStatic: i === 0 || i + 1 === listEls.length
		    		})
		    		const randomAngularV = () => (Math.random()>0.5)? 1 : -1;
		    		//Body.setAngularVelocity( box, Math.PI/1000*randomAngularV());
		    		return box
		    	}
		    )
		    let stack2 = []

			if (listDummies.length !== 0) 
			{
				stack2 = Matter.Composites.stack(
			  		// xx, yy, columns, rows, columnGap, rowGap, cb
			  		0, 0, listDummies.length, 1, 0, 0,
			  		(xx, yy, i) => {
			  			const randomX = Math.floor((Math.random() * (this.state.width-200)) + 1)
			    		const {y, width, height} = listDummies[i].getBoundingClientRect();
			    		const box = Matter.Bodies.rectangle(randomX, -this.state.height+200, width, height, {
			      			//isStatic: i === 0 || i + 1 === listDummies.length
			    		})
			    		const randomAngularV = () => (Math.random()>0.5)? 1 : -1;
			    		//Body.setAngularVelocity( box, Math.PI/6*randomAngularV());
			    		return box
			    	}
		    	)
			}
		    

		    const wallThickness = 100
			const wallHeight = this.state.height*2
			const wallY = 0

		    const floor = Matter.Bodies.rectangle(this.state.width/2,this.state.height-50,this.state.width,100)
		    const wallR = Matter.Bodies.rectangle(this.state.width+50,wallY,wallThickness,wallHeight)
		    const wallL = Matter.Bodies.rectangle(-50,wallY,wallThickness,wallHeight)
		    const ceil  = Matter.Bodies.rectangle(this.state.width/2,-this.state.height-50,this.state.width,100)
		    Body.setStatic(floor, true)
		    Body.setStatic(wallR, true)
		    Body.setStatic(wallL, true)
		    Body.setStatic(ceil,  true)

			const mouseConstraint = Matter.MouseConstraint.create(
	  			engine, {element: document.querySelector("#mjs-wrapper")}
			)


			Matter.World.add(engine.world, [stack1, stack2, mouseConstraint, floor, wallL, wallR, ceil])
			listEls.forEach(e => {
		  		e.style.position = "absolute";
		  		e.addEventListener("click", e => {
		    		console.log(e.target.textContent)
		    	})
			});

			(function update() {
		  		requestAnimationFrame(update)
			  	stack1.bodies.forEach((block, i) => {
				    const li = listEls[i]
				    const {x, y} = block.vertices[0]
				    li.style.top = `${y}px`
				    li.style.left = `${x}px`
				    li.style.transform = `translate(-50%, -50%) 
			                             rotate(${block.angle}rad) 
			                             translate(50%, 50%)`
				});
				stack2.bodies.forEach((block, i) => {
				    const li = listDummies[i]
				    const {x, y} = block.vertices[0]
				    li.style.top = `${y}px`
				    li.style.left = `${x}px`
				    li.style.transform = `translate(-50%, -50%) 
			                             rotate(${block.angle}rad) 
			                             translate(50%, 50%)`
				});
	  			Matter.Engine.update(engine)
			})();
		}
  	
		runCode()
		this.update()
	}

	update = () => {
    	this.setState({
	      	height: window.innerHeight,
	      	width: window.innerWidth
    	})
   	}
   	
   	closeMenu = () => {
   		this.props.menudrop({menuDrop:false}) 
   	}
	
	render(){
	  	return (
			<div id="mjs-wrapper" style={this.style.wrapper}>
				{this.dummies()}
			    <MenuLink imgUrl="IMG/aquarell.jpg" href={"/"} titel="Aquarelle" beschreibung="Experimente mit Wasser und Farbe" />
			    <MenuLink imgUrl="IMG/objekt.png" href={"/login"} titel="Objekte" beschreibung="Recyceltes aus dem frühen Industriezeitalter" />
			    <MenuLink imgUrl="" href={"/artist"} titel="Künstlerprofil" beschreibung="Informationen über die Künstlerin" />
			</div>
		)
	}
}
export default Scene
