import React from 'react'
import Scene from './drop' 

const Einzelbild = props => {
	
	// props.setEinzelAnsicht closes EBA
	// props.bildinfos contains infos

	const style = {
		wrapper: {
			height: '100vh',
			width: '100vw',
			background:`url(${props.bildinfos.source})`,
			position:'fixed',
			left:0,
			top:0,
			zIndex:3,
			//backgroundImage:props.
		},
		info: {
			height: 200,
			width:'100vw',
			position:'absolute',
			background:'rgba(255,255,255,0.88)',
			bottom:0
		},
		leftside: {left:0,width:'50vw'},
		rightside: {right:0,width:'50vw'},
		closebutton: {display:'flex',alignItems:'center',justifyContent:'center',cursor:'pointer',width:50,height:50,position:'fixed',top:100,right:50,background:'#323232',color:'rgb(240,240,240)'},
		x:{fontSize:'1.5em'}
	}
	return (
		<div className="einzelbild" style={style.wrapper}>
			<div className="closebutton" style={style.closebutton} onClick={props.schliessen}><p style={style.x}>X</p></div>
			<div className="infobereich" style={style.info}></div>
			<div className="rightSide" style={style.leftside}>
				<div></div>
				<div></div>
			</div>
			<div className="leftSide" style={style.rightside}>
				<div></div>
				<div></div>
			</div>
		</div>
	)
}

export default Einzelbild
