import React from 'react'
import { hslaStr, randomSize } from './utils/helpers'

const Dummy = (props) => {

	const dummiesStyle = () => {
	    const randmSzStr = randomSize(25,55)
	}
	
	style = {
		width:randmSzStr,
		height:randmSzStr,
		background:'hsla('+hslaStr()+')',
		top:-5000,
		left:20,
		position:'absolute',
		"&:hover": {
			opacity:0.5
		}
	}

	const switchColor = (e) => {
		e.preventDefault()
		const color = e.target.style.background
		const tar = e.target
		const el = document.getElementById("mjs-wrapper")
		const origColor = el.style.background
		el.style.background = color
		e.target.style.background = "lightgrey"
		const rePaint = (element, target, color) => {
			element.style.background = origColor
			target.style.background = color
		}
		setTimeout(rePaint,500,el,tar,color)
	}
	return (
		<div className='blocks dummies super-delayed-fallable xposrandom' onTouchStart={switchColor} onMouseDown={switchColor} style={dummiesStyle()}></div>
	)
}

export default Dummy
