import React, { useState } from 'react'
import ZurWand from './../components/zurwand'
import Einzelbild from './../components/einzelbild'

// hier m√ºssen Bilder geladen werden
// const bilder = []
// bilder.map(...) erstelle <BildThumb />


const Bilderwand = props => {
	const [einzelAnsicht, setEinzelAnsicht] = useState(false)
	const [selectedArt, setSelectedArt] = useState(0)

	// ### click auf Bild -> setEinzelAnsicht! && set Bildinfos

	const style = {
		header:{
			display:'flex',
			flexDirection: 'row',
    		alignItems: 'center',
    		justifyContent: 'space-between',
    		top: 0,
    		height: 64,
    		position: 'fixed',
    		zIndex: 1
		},
		heading: {
			height: 36,
			zIndex:2,
			background:'#333333',
			color:'lightgrey',
			pointerEvents: 'none',
			fontSize:'1.5em'
		},
		wand:{
			background:'#2b2b2b',
			height:'100vh',
			width:'100%',
			position: 'fixed',
		    top: 0,
		    left: 0
		},
		arrowcontrols:{
			position:'absolute',
			zIndex:3,
			color:'rgb(240,240,240)',
			display:'flex',
			alignItems:'center',
			left:50,
			height:'100%',
			top:0,
			width:'calc(100% - 100px)',
			justifyContent:'space-between',
			fontSize:'2em',
			flexDirection:'row-reverse'
		},
		arrow:{
			opacity:'0.6',
			transition:'opacity 0.2s ease',
			fontWeight:900,
			cursor:'pointer'
		},
		imagewrapper:{
			position:'absolute',
			top:0,
			width:'100vw',
			height:'100%',
			display: 'flex',
		    flexDirection: 'row',
		    alignItems: 'center',
		    justifyContent: 'center'
		},
		image:{
			cursor:'pointer',
			display:'inline',
			opacity:0,
			animationName:'fadein',
			animationDuration:'0.4s',
			animationDelay:'1s',
			animationTimingFunction: 'ease',
			animationFillMode: 'forwards',
			height:'70%',
			webkitBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1), ",
			mozBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1)",
			boxShadow: "0 0 10px rgba(0,0,0,1),0 0 30px rgba(0,0,0,0.85),0 0 40px rgba(0,0,0,0.45),0 0 50px rgba(0,0,0,0.3),0 0 60px rgba(0,0,0,0.25)"
		},
		bildtitel:{
			color: 'lightgrey'
		}
	}

	// clickEvent auf IMG, 
	// dann <p className="bild_titel" style={style.bildtitel}>{props.bilder[0].titel}</p> 
	// unter Einzelbildansicht (EBA)

	const bilder = props.bilder
	/*
		[i]
			.titel
			.beschreibung
			.preis
			.tags
			.groesse.x/.y/.z
			.gewicht
			.altSrc // [filename2,filename3]
			.source
			.alt#txt
			.verkauft // true,false
	*/

	const diesesKunstwerk = (el) => {
		setSelectedArt(el.target.id)
		setEinzelAnsicht(true)
	}

	const bilderHTML = bilder.map((bild) => {
		const left = bild.id*100

		const obj = JSON.stringify(style.imagewrapper)
		const newstyle = JSON.parse(obj)
		newstyle.left=left+'%'
        return (<div key={bild.id} className="bilderwrapper" style={newstyle}><img id={bild.id} className="wandbild" style={style.image} alt={bilder[bild.id].altTxt} src={bilder[bild.id].source} onClick={(el)=>{diesesKunstwerk(el)}}/></div>)
    })


    const arrowcontrols = (bilder) =>{
    	if (bilder.length > 0) {
    		const arrows = document.querySelectorAll('.arrow')
	
			const arrownavigation =()=> {
				alert(e)
			}
	    	return(
		    	<div className="arrowcontrols" style={style.arrowcontrols}>
			    	<style>
	  					{`.arrow:hover{opacity:1 !important}`}
					</style>
					<div className="arrow" onClick={arrownavigation} id="rightarrow" style={style.arrow}>&gt;</div>
					<div className="arrow" onClick={arrownavigation} id="leftarrow" style={style.arrow}>&lt;</div>
				</div>
			)
		}
    } 

	return (
		<div className="sctn sctn_bilderwand">
			<div className="sctn_header" style={style.header}>
				<h1 className="sctn_heading" style={style.heading}>{props.titel}</h1>
				<ZurWand label="Anmerkungen" wandLaden={() => {console.log(`Wand ${props.titel} wird geladen...`)}}>Anmerkungen der K√ºnstlerin</ZurWand>
			</div>
			<div className="sctn_body">
				{arrowcontrols(bilder)}
				<div className="sctn_wand" style={style.wand}>
					{bilderHTML}
				</div>
			</div>
			{einzelAnsicht && <Einzelbild bildinfos={bilder[selectedArt]} schliessen={()=>{setEinzelAnsicht(false)}} />}
		</div>
	)
}

export default Bilderwand
