import React from 'react'
import { Card } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import { useNavigate } from 'react-router-dom'
import { hslaStr } from './utils/helpers'

const MenuLink = (props) => {

	const navigate = useNavigate()
	const resetStyle = () => {
		const delayed_fallables = document.querySelectorAll(".delayed-fallable")
  		delayed_fallables.forEach((i) => {
			// DEBUG: console.log(i)
			i.style.position = 'initial'
			i.style.left = 'auto'
			i.style.top = 'auto'
			i.style.transform = 'rotate(0rad)'
		})
	}
	const links = (e) => {console.log(e.target.classList.contains('clickable'));if(e.target.classList.contains('clickable')){props.setRemoveNonMenuHtml();e.target.classList.remove('clickable');navigate('/');navigate(props.href);resetStyle()}}

	const styles = {
		root: {
			height: 90,
			textAlign: 'center',
			display: 'flex',
			alignItems: 'center',
			textDecoration:"none",
			width: 300,
				padding: '1em',
				cursor: 'pointer',
				pointerEvents: 'all',
				background:'lightgrey',
				color: '#000',
				"&:hover": {
				background:'#f1f1f1',
				color:'#545454'
			}
		}
	}
	const useStyles = makeStyles(styles)
	const classes = useStyles()

	return (
		<Card 
			onTouchEnd={(el) => {links(el)}}
			onClick={(el) => {el.preventDefault();links(el)}} 
			className={classes.root+" blocks menulink fallable noselect xposrandom"}
		>	
			<h1>{props.titel}</h1>
		</Card>
	)
}

export default MenuLink
