import React from 'react'
import ZurWand from './../components/zurwand'

// hier müssen Bilder geladen werden
// const bilder = []
// bilder.map(...) erstelle <BildThumb />


const Bilderwand = (props) => {
	
	const style = {
		header:{
			display:'flex',
			flexDirection: 'row',
    		alignItems: 'center',
    		justifyContent: 'space-between',
    		position: 'absolute',
    		top: 0,
    		height: 64,
    		position: 'fixed',
    		zIndex: 1
		},
		heading: {
			height: 36,
			zIndex:2,
			background:'#333333',
			color:'lightgrey',
			pointerEvents: 'none',
			fontSize:'1.5em'
		},
		wand:{
			background:'#2b2b2b',
			height:'100%',
			width:'100%',
			position: 'fixed',
		    top: 0,
		    left: 0,
		    display: 'flex',
		    flexDirection: 'column',
		    alignItems: 'center',
		    justifyContent: 'center'
		},
		image:{
			height:'70%',
			webkitBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1), ",
			mozBoxShadow: "0px 0px 52px 0px rgba(0, 0, 0, 1)",
			boxShadow: "0 0 10px rgba(0,0,0,1),0 0 30px rgba(0,0,0,0.85),0 0 40px rgba(0,0,0,0.45),0 0 50px rgba(0,0,0,0.3),0 0 60px rgba(0,0,0,0.25)"
		},
		bildtitel: {
			color: 'lightgrey'
		}
	}
	
	const bilderSerie = []

	// clickEvent auf IMG, 
	// dann <p className="bild_titel" style={style.bildtitel}>{props.bilder[0].titel}</p> 
	// unter Einzelbildansicht (EBA)

	return (
		<div className="sctn sctn_bilderwand">
			<div className="sctn_header" style={style.header}>
				<h1 className="sctn_heading" style={style.heading}>{props.titel}</h1>
				<ZurWand label="Anmerkungen" wandLaden={() => {console.log(`Wand ${props.titel} wird geladen...`)}}>Anmerkungen der Künstlerin</ZurWand>
			</div>
			<div className="sctn_body">
				<div className="sctn_wand" style={style.wand}>
					<img style={style.image} src={props.bilder[0].source} />
				</div>
			</div>
		</div>
	)
}

export default Bilderwand
