import React from 'react'

const Dummy = (props) => {

	randomHue = () => {return Math.floor((Math.random() * 360) + 0);}

	hsla = hue => {return `${hue},43%,62%,1`}

	breitehoehe = () => {return  Math.floor((Math.random() * 120) + 50);}

	const dummiesStyle = () => {
	    	const breiteHoehe = this.breitehoehe() 
	    	console.log(breiteHoehe)
	    	const hslaStr = this.hsla(this.randomHue())
	    	return {width:breiteHoehe,height:breiteHoehe,background:'hsla('+hslaStr+')',top:0,left:20,position:"absolute"}
	}

	const switchColor = (e) => {
		const color = e.target.style.background
		let tar = e.target
		let el = document.getElementById("mjs-wrapper")
		el.style.background = color
		e.target.style.background = "lightgrey"
		const rePaint = (element, target, color) => {
			element.style.background = "#ffffff"
			target.style.background = color
		}
		setTimeout(rePaint, 500, el,tar,color)
	}
	return (
		<div className='blocks dummies' onTouchStart={switchColor} onMouseDown={switchColor} className={props.classnames} style={dummiesStyle}></div>
	)
}

export default Dummy
