import React, {Component, useRef} from 'react'
import AppBar from '@material-ui/core/AppBar'
import MyToolbar from './myToolbar'
import MenuButton from './menuButton'
import AdminButton from './adminButton'
import Scene from './scene' 

const outerStyleL = {
	width:"50%",display:"inline-block",position:"relative",left:"0"
}
const outerStyleR = {
	width:"50%",display:"inline-block",position:"relative",right:"0"
}
const innerStyleL = {display:"flex", justifyContent:"flex-start"}
const innerStyleR = {display:"flex", justifyContent:"flex-end"}

export const Menu = () => {
	
	const sceneEl = useRef(null)

  	const handleClick = () => {
  		sceneEl.current.style.display === "none" ? sceneEl.current.setStyle({display:"block"}) : sceneEl.current.setStyle({display:"none"}) 
  	}

	return (
		<>
		<AppBar>
			<MyToolbar>
				<h1 className="page_titel">Galerie Sens</h1>
				<div style={outerStyleL}>
					<div style={innerStyleL}>
						<MenuButton onPress={handleClick} />
					</div>
				</div>
				<div style={outerStyleR}>
					<div style={innerStyleR}>
						<AdminButton href="/" text="Startseite" />
						<AdminButton href="/registrierung" text="Registrieren" />
						<AdminButton href="/login" text="Login" />
					</div>
				</div>
			</MyToolbar>
		</AppBar>
		<Scene ref={sceneEl} style={{display:"none"}} />
		</>
	)
}

export default Menu
