import React, {useEffect, useState, setState} from "react";
import Matter from "matter-js";

class Scene extends React.Component {
  constructor() {
    super();
    this.state = {
      height:0,
      width:0
    };
  }

  /**
   * Calculate & Update state of new dimensions
   */
  updateDimensions() {
    if(window.innerWidth < 500) {
      this.setState({ width: 450, height: 102 });
    } else {
      let update_width  = window.innerWidth
      let update_height = window.innerHeight
      this.setState({ width: update_width, height: update_height })
    }
  }





  /**
   * Add event listener
   */
  componentDidMount() {
    
    this.updateDimensions();
    
    window.addEventListener("resize", this.updateDimensions.bind(this));
    console.log(this.state.width)
    var Engine = Matter.Engine,
        Render = Matter.Render,
        World = Matter.World,
        Bodies = Matter.Bodies,
        Mouse = Matter.Mouse,
        MouseConstraint = Matter.MouseConstraint;

    var engine = Engine.create({
      // positionIterations: 20
    });

    var render = Render.create({
      element: this.refs.scene,
      engine: engine,
      options: {
        width:  Scene.state.width,
        height: Scene.state.height,
        wireframes: false
      }
    });

    const xPos = this.state.width-25

    var rectA = Bodies.rectangle(410, 100, 120, 50, { restitution: 0.5 });
    var rectB = Bodies.rectangle(110, 50, 80, 50, { restitution: 0.5 });
    var rectC = Bodies.rectangle(70, 50, 140, 50, { restitution: 0.5 });
    var rectD = Bodies.rectangle(300, 50, 90, 50, { restitution: 0.5 });
    World.add(engine.world, [
      // walls
      Bodies.rectangle(this.state.width/2, 700, this.state.width, 50, { isStatic: true }),
      Bodies.rectangle(xPos, 350, 50, 700, { isStatic: true }),
      Bodies.rectangle(25, 350, 50, 700, { isStatic: true })
    ]);

    World.add(engine.world, [rectA, rectB, rectC, rectD]);

    // add mouse control
    var mouse = Mouse.create(render.canvas),
      mouseConstraint = MouseConstraint.create(engine, {
        mouse: mouse,
        constraint: {
          stiffness: 0.2,
          render: {
            visible: false
          }
        }
      });

    World.add(engine.world, mouseConstraint);

    Matter.Events.on(mouseConstraint, "mousedown", function(event) {
      World.add(engine.world, Bodies.rectangle(event.mouse.mousedownPosition.x, 50, 120, 50, { restitution: 0.7 }));
    });

    Engine.run(engine);

    Render.run(render);
   
  }


  render() {
    return <div ref="scene" />;
  }
}
export default Scene;
